        <script type="text/javascript">
            const burger = document.querySelector('.header__burger')
            const questionsItem = document.querySelectorAll('.section-questions__item')
            if (burger){
                burger.onclick = function (){
                    document.body.classList.toggle('_burger')
                }
            }
            if (questionsItem.length > 0) {
                questionsItem.forEach(element => {
                    element.onclick = function (e) {
                        this.classList.toggle('_active')
                    }
                });
            }
            let topScroll = 0
            const up = document.querySelector('.up')
            if (up) {
                window.onscroll = () => {
                    topScroll  = window.scrollY
                    up.classList.toggle('_active', topScroll > 500)
                }
                up.onclick = scrollStart
                function scrollStart (){
                    if(topScroll > 0){
                        setTimeout(function(){
                            window.scrollTo({top: topScroll})
                            scrollStart ()
                            topScroll = topScroll-50;		
                        }, 0) 
                    }
                }
            }
            //---------------------------------------------------------------------
            //---------------------------scrolled NAV------------------------------
            const nav = document.querySelectorAll('.menu__nav')
            function scrollNav(item) {
                let topElement = offsetPosition(item)
                topScroll < topElement ? scrollNavDown(item) : scrollNavUp(item)
                function scrollNavDown(item){
                    if(topScroll < topElement){
                        setTimeout(function(){
                            window.scrollTo({top: topScroll})
                            scrollNavDown(item)
                            topScroll = topScroll+50;		
                        }, 0)
                    } 
                }
                function scrollNavUp(item){
                    if(topScroll > topElement){
                        setTimeout(function(){
                            window.scrollTo({top: topScroll})
                            scrollNavUp(item)
                            topScroll = topScroll-50;		
                        }, 0)
                    }
                }
            }
            function offsetPosition(element) {
                let offsetTop = 0;
                do {
                    offsetTop += element.offsetTop;
                } while (element = element.offsetParent);
                return offsetTop;
            }
            if (nav) {
                nav.forEach(element => {
                    element.onclick = function (e) {
                        e.preventDefault();
                        if (element.dataset.nav) {
                            let menu = document.querySelector(`.${element.dataset.nav}`)
                            menu.removeAttribute('id')
                            scrollNav(menu)
                            setTimeout(function(){
                                document.body.classList.remove('_burger')
                            }, 200)
                        }
                        window.location.hash = element.dataset.nav
                    }
                })
            }
            //----------------------------------------------------------------------
            //----------------------------------------------------------------------
            let contactUs = document.querySelectorAll('.contact-us')
            if(contactUs){
                contactUs.forEach(element => {
                    element.onclick = function (e) {
                        e.preventDefault();
                        modal()
                        setTimeout(function(){
                            document.body.classList.remove('_burger')
                        }, 200)
                    }
                })
            }
            function modal(){
                let modalWrap = document.querySelector(`.modal`)
                let close = document.querySelector('.modal__close')
                let html = document.querySelector(`html`)
                let header = document.querySelector(`.header`)
                let widthScroll = _scrollWidth()

                if (!modalWrap.classList.contains(`done`)) {
                    _modalClose(modalWrap, html, header)
                } else {

                    html.style.marginRight = `${widthScroll}px`
                    html.style.overflowY = 'hidden'

                    modalWrap.onclick = function(e) {
                        if (e.target.classList.contains('modal')) _modalClose(modalWrap, html, header)
                    }

                    close.onclick = function() {
                        _modalClose(modalWrap, html, header)
                    }

                    document.addEventListener('keydown', function(e) {
                        if (e.keyCode == '27') _modalClose(modalWrap, html, header) 
                    })

                    setTimeout(function(){
                        modalWrap.classList.remove('done');
                    }, 200)
                }

                function _modalClose(modalWrap, html, header) {
                    modalWrap.classList.add('done')
                    setTimeout(function(){
                        html.style.overflowY = ''
                        html.style.marginRight = ''
                        header.style.right = ''
                    }, 1000)
                }

                function _scrollWidth() {
                    let div = document.createElement('div');
                    div.style.overflowY = 'scroll';
                    div.style.width = '50px';
                    div.style.height = '50px';
                    document.body.append(div);
                    let windowWidth = document.body.offsetWidth;
                    let scrollWidth = div.offsetWidth - div.clientWidth;
                    if (windowWidth > 900) {
                        scrollWidth = div.offsetWidth - div.clientWidth;
                    } else {
                        scrollWidth = 0;
                    }
                    div.remove();
                    return scrollWidth;
                }
            }


            window.onload = (event) => {
                // localStorage.removeItem("timeleft");
                setreferrer_cookie();
                // clear_localStorage();
            };
        </script>
